YUI.add("moodle-block_filtered_course_list-accordion",function(e,t){"use_strict";var n;M.block_filtered_course_list=M.block_filtered_course_list||{},n=M.block_filtered_course_list.accordion={},n.init=function(t){e.on("domready",function(){sectionTitles=e.all(".block_filtered_course_list .course-section"),sectionTitles.each(function(e){e.addClass("collapsed");var t=e.getHTML();e.setHTML('<a href="#">'+t+"</a>"),e.on("click",function(e){e.preventDefault(),n.toggle(this)})})})},n.toggle=function(e){e.hasClass("collapsed")?(e.removeClass("collapsed"),e.addClass("expanded")):e.hasClass("expanded")&&(e.removeClass("expanded"),e.addClass("collapsed"))}},"@VERSION@");
